imagecopyresampled($thumb, $water, 0, 0, 0, 0, $new_width, $new_height, $w_w, $w_h);
imagepng($thumb, $waterpic);
imagedestroy($water);
$water = imagecreatefrompng($waterpic);
$w_w = imagesx($water);
$w_h = imagesy($water);
}
$b_w = imagesx($back);
$b_h = imagesy($back);
imagecopy($back, $water, $x, $y - $w_h , 0, 0, $w_w, $w_h);
imagejpeg($back, "./files/test.jpg");
imagedestroy($back);
imagedestroy($water);
}
```

